import Settings from './interface/Settings'
import Results from './interface/Results'

/**
 *	Interface between the client side and the back-end.
 *
 *	The interface has settings and a socket enabling it 
 *	to send and receive data from the server running the
 *	Java MAS simulation.
 */

export default class Interface{  

	/**
	 * Constructor setting fields and listeners.
	 */
	constructor(){

		// Fields

		/**
		 * Socket to communicate with the backend.
		 * @type {Socket}
		 */
		this.socket = io.connect(`http://${window.location.hostname}:8083`)
		/**
		 * Settings of the whole environnement.
		 * @type {Settings}
		 */
		this.settings = new Settings(this)
		/**
		 * Results handler for calculations from the back-end.
		 * @type {Results}
		 */
		this.results = new Results(this)
		/**
		 * Flag signalant si une simulation est en cours ou non.
		 * @type {Boolean}
		 */
		this.simulationRunning = false

		// Socket listeners

		this.socket.on('connect', () => {

			console.info('Connection to the remote server established.')

		})
	}

	/**
	 *	Start the simulation by sending the settings to the back-end
	 *	along the message 'startSimulation'.
	 *
	 *  @return {Interface} chaining
	 */

	startSimulation(){

		this.simulationRunning = true

		this.socket.on('loading', data => this.results.loading(data.progress))

		this.results.loading(0)

		this.socket.emit('startSimulation', this.settings.getSettings(), results => {

			console.log(results)

			if (!this.simulationRunning)
				return

			if (results.error)
				return this.results.error(results.error)

			this.results.showResults(results.data)

		})

		return this

	}

	/**
	 *	Stop the client-side simulation by removing the loading screen and
	 *	blocking results callback.
	 *
	 *  @return {Interface} chaining
	 */

	stopSimulation(){
		this.simulationRunning = false

		this.socket.removeListener('loading')

		this.socket.emit('cancel')

		return this
	}
}

import LiveSimulation from './results/LiveSimulation'

/**
 * Deals with the results sent by the server.
 */
export default class Results{

	/**
	 * @param  {Interface} iface - Interface object using this results.
	 */
	constructor(iface){
		/**
		 * Interface object using this results.
		 * @type {Interface}
		 */
		this.interface = iface

		/**
		 * The data computed by the server.
		 * @type {Object}
		 */
		this.data = null
		window.results = this
	}

	/**
	 * When an error is received, prints it to screen.
	 * 
	 * @param  {string} err - the received error
	 * @return {Results} chaining
	 */
	error(err){

		console.error(`Error: ${err}`)

		$('#modal-results p').html(`

			<div class="center">
				Error encountered while computing the results: <br>
				${err}
			</div>
		`).modal('open')

		return this
	}

	/**
	 * When the server is processing, show the progress.
	 *
	 * @param {Number | boolean} percent - The loading progression or false (== 0) if it just started.
	 * @return {Results} chaining
	 */
	loading(percent = false){

		$('#modal-results p').html(`

			<div class="center">
				Please wait while our server is computing the results.
			</div>

			<div class="progress">
				<div class="${ percent ? `determinate" style="width: ${percent}%"` : 'indeterminate"'}></div>
			</div>
		`).modal('open')

		return this
	}

	/**
	 * When everything is okay, display paths, stats and show a simulation.
	 *
	 * @param {Object} data - The results object sent by the server.
	 * @return {Results} chaining
	 */
	showResults(data){

		this.data = data

		data.patrols.forEach(patrol => {
			patrol.label = patrol.reduce((sum, target) => `${sum}${target} â‡’ `, '').slice(0,-3)
		})

		console.info('Results received.')

		// Building the list of patrols.

		let patrolsTableHTML = `
			<table class="striped centered">
				<thead>
					<tr>
						<th>Patrol ID</th>
						<th>path</th>
					</tr>
				</thead>

				<tbody>`

		data.patrols.forEach((patrol, index) => {

			patrolsTableHTML += `
				<tr>
					<td>${index}</td>
					<td>${patrol.label}</td>
				</tr>`
		})

		patrolsTableHTML += `
				</tbody>
			</table>`

		// We have to find the best strategy.

		const statisticsTable = []

		data.strategies.forEach(strategy => {

			const averageGuardianUtility = strategy.iterations.reduce((sum, iteration) => sum+iteration.guardianUtility, 0) / strategy.iterations.length
			const averageRobberUtility = strategy.iterations.reduce((sum, iteration) => sum+iteration.robberUtility, 0) / strategy.iterations.length

			statisticsTable.push({
				iterations: strategy.iterations,
				probabilities: strategy.probabilities.reduce((sum, probability) => `${sum}${probability.toFixed(2)} | `, '').slice(0, -3),
				guardianUtility: averageGuardianUtility,
				robberUtility: averageRobberUtility,
				strategy: strategy
			})

		})

		const sortedStatisticsTable = statisticsTable.sort((s1, s2) => s2.guardianUtility - s1.guardianUtility)

		const bestStreategy = sortedStatisticsTable[0].strategy

		// We feed the chart with average evolution for the best strategy.

		let chartData = []
		let sum = 0

		sortedStatisticsTable[0].iterations.forEach(iteration => {

			chartData.push({
				x: chartData.length,
				y: (sum+=iteration.guardianUtility)/(chartData.length+1)
			})

		})

		// Building the list of statistics.

		let statisticsTableHTML = `
			<table class="striped centered">
				<thead>
					<tr>
						<th>Probabilities</th>
						<th>Guardian utility</th>
						<th>Robber utility</th>
					</tr>
				</thead>

				<tbody>`

		sortedStatisticsTable.forEach(strategy => {

			statisticsTableHTML += `
				<tr>
					<td>${strategy.probabilities}</td>
					<td>${Number(strategy.guardianUtility).toFixed(4)}</td>
					<td>${Number(strategy.robberUtility).toFixed(4)}</td>
				</tr>`
		})

		statisticsTableHTML += `
				</tbody>
			</table>`

		$('#modal-results p').html(`

			<div class="row">
				<div class="col s12">
					<ul class="tabs">
						<li class="tab col s3"><a class="active" href="#chart">Chart</a></li>
						<li class="tab col s3"><a href="#visualization">Visualization</a></li>
						<li class="tab col s3"><a href="#patrols">Patrols</a></li>
						<li class="tab col s3"><a href="#statistics">Statistics</a></li>
					</ul>
				</div>
				<div id="chart" class="col s12">
					<canvas width="100%" height="400" id="line-chart"></canvas>
				</div>
				<div id="visualization" class="col s12">
					<div id="liveSimulationLog">Iteration running...</div>
					<div id="liveSimulation" class="s12">
					</div>
				</div>
				<div id="patrols" class="col s12">
					${patrolsTableHTML}
				</div>
				<div id="statistics" class="col s12">
					${statisticsTableHTML}
				</div>
			</div>

		`).modal('open')

		$('#modal-results p ul.tabs').tabs()

		const scatterChart = new Chart("line-chart", {
			type: 'line',
			data: {
				datasets: [{
					label: 'Best strategy utility over time.',
					data: chartData
				}]
			},
			options: {
				maintainAspectRatio: false,
				scales: {
					xAxes: [{
						type: 'linear',
						position: 'bottom'
					}]
				}
			}
		})

		$('#export-gambit').removeAttr('disabled')

		new LiveSimulation(this, data, bestStreategy, '#liveSimulation').run()

		return this

	}

	/**
	 * Download a Gambit version of the server-computed paths.
	 * /!\ Asynchronous as it relies on Saver::download!
	 * 
	 * @return {Results} chaining
	 */
	exportGambit(){

		const distanceWeight = parseInt($('#distanceWeight').val())
		const settings = this.interface.settings.getSettings()

		const patrols = this.data.patrols
		const robbers = this.interface.settings.robbers.getSettings().list
		const vertices = settings.paths.vertices.filter(vertex => vertex.id != 0)

		let patrolsListString = patrols.reduce((sum, patrol) => `${sum} "${patrol.label}"`,'')

		let attacksListString = ''

		const attacks = []

		robbers.forEach(robber => {

			vertices.forEach(vertex => {

				attacksListString += `"Robber type ${robber} attacking ${vertex.id}" `

				attacks.push({robber: robber, target: vertex})

			})

		})

		let rewardsString = ''

		let numberString = [...Array(patrols.length * robbers.length * vertices.length).keys()].reduce((sum,index) => `${sum}${index+1} `, '')

		attacks.forEach(attack => {

			patrols.forEach(patrol => {

				let distance = 0

				if (patrol.includes(attack.target.id))
					for (let n = 0; patrol[n] !== attack.target.id; n++)
						distance += settings.paths.edges.find(edge => (edge.source === patrol[n] && edge.target === patrol[n+1]) || (edge.source === patrol[n+1] && edge.target === patrol[n])).length
				else 
					distance = Infinity

				console.log(patrol.label)
				console.log(attack.target.id)
				console.log(distance)

				const catchProbability = settings.robbers.catchProbability[''+attack.robber] * distanceWeight/( distance + distanceWeight )

				console.log(attack, catchProbability)

				const guardianUtility = catchProbability * attack.target.guardiansReward - (1 - catchProbability) * attack.target.guardiansCost

				const robberUtility = - catchProbability * attack.target.robberSettings[attack.robber].cost + (1 - catchProbability) * attack.target.robberSettings[attack.robber].reward

				rewardsString += `{ "${patrol.label} vs ${attack.robber} - ${attack.target.id}" ${guardianUtility}, ${robberUtility} }\n`

			})

		})

		const nfg = 
`NFG 1 R "Patrolling game" { "Guardian" "Robber" }

{ { ${patrolsListString}}
{ ${attacksListString}}
}
""

{
${rewardsString}}
${numberString}
`

		const date = new Date()

		this.interface.settings.saver.download(
			`${date.toLocaleDateString()}-${date.toLocaleTimeString().replace(':', '-')}.nfg`,
		nfg)

		return this
	}

}

import Graph from './settings/subsettings/Graph.js'
import Robbers from './settings/subsettings/Robbers.js'
import Saver from './settings/files/Saver'
import Loader from './settings/files/Loader'

/**
 * Settings of the simulation.
 *
 * Initialize settings with default values.
 */

export default class Settings {

	/**
	 * @param  {Interface} iface - Interface object using this settings.
	 */
	constructor(iface){

		// Fields

		/**
		 * Interface object using this settings.
		 * @type {Interface}
		 */
		this.interface = iface

		/**
		 * The graph configuration.
		 * @type {Graph}
		 */
		this.graph = new Graph(this)

		/**
		 * The robbers configuration.
		 * @type {Robbers}
		 */
		this.robbers = new Robbers(this)

		/**
		 * A class enabling us to save the configuration (or any text file) on the client's computer.
		 * @type {Saver}
		 */
		this.saver = new Saver(this)

		/**
		 * A class enabling us to load a configuration from the client's computer.
		 * @type {Loader}
		 */
		this.loader = new Loader(this)

		// Default values

		this.init()
		this.loader.loadDefault()

	}

	/**
	 * (Re)-Initialize every sub-settings. 
	 * 
	 * @return {Settings} chaining
	 */
	init(){
		this.graph.init()
		this.robbers.init()
		$('#numberOfIterations').val(20)

		return this
	}

	/**
	 * Return settings as as JSON object.
	 *
	 * Those settings can be sent to the backend.
	 *
	 * @return {Object} JSON-ed concatenated settings.
	 */

	getSettings(){
		return {
			general: this.getGeneralSettings(),
			paths: this.graph.getSettings(),
			robbers: this.robbers.getSettings()
		}
	}

	/**
	 *	Concatenate the general settings in one 
	 *	JSON object.
	 *
	 * @return {Object} concatenated settings.
	 */

	getGeneralSettings(){
		return {
			numberOfIterations: parseInt($('#numberOfIterations').val()),
			distanceWeight: parseInt($('#distanceWeight').val())
		}
	}
}

/**
 * Class enabling us to play a live simulation of the best strategy 
 * sent by the server.
 */

export default class LiveSimulation{

	/**
	 * @param  {Results} results - Results object that instanciated this simulation.
	 * @param  {Object} computedData - Data computed by the server.
	 * @param  {Object} bestStrategy - Strategy previously built.
	 * @param  {string} selector - jQuery selector for where to put the simulation.
	 */
	constructor(results, computedData, bestStreategy, selector){

		/**
		 * Results object that instanciated this simulation.
		 * @type {Results}
		 */
		this.results = results

		/**
		 * Original cytoscape graph.
		 * @type {cytoscape}
		 */
		this.originalCy = this.results.interface.settings.graph.cy
		window.liveSimulation = this

		/**
		 * Data computed by the server.
		 * @type {Object}
		 */
		this.computedData = computedData

		/**
		 * Strategy previously built.
		 * @type {Object}
		 */
		this.bestStreategy = bestStreategy

		/**
		 * jQuery selector for where to put the simulation.
		 * @type {string}
		 */
		this.selector = selector

		/**
		 * LiveSimulation graph stylesheet. 
		 * @type {Array<Object>}
		 */
		this.stylesheet = [{
				selector: 'node',
				style: {
					height: 20,
					width: 20
				}
			},
			{
				selector: 'edge',
				style: {
					'curve-style': 'haystack',
					'haystack-radius': 0,
					width: 5,
					opacity: 0.5,
					'line-color': 'grey'
				}
			},
			{
				selector: '.base',
				style: {
					'background-color': '#61bffc'
				}
			},
			{
				selector: '.secured',
				style: {
					'background-color': '#81c784'
				}
			},
			{
				selector: '.robbed',
				style: {
					'background-color': '#e57373'
				}
			},
			{
				selector: '.caught',
				style: {
					'background-color': '#E57373'
				}
			},
			{
				selector: '.guardian',
				style: {
					height: 40,
					width: 40,
					'background-image': '/img/guardian-40.png',
					'background-opacity': 0
				}
			},
			{
				selector: '.robber',
				style: {
					height: 40,
					width: 40,
					'background-image': '/img/robber-40.png',
					'background-opacity': 0
				}
			}
		]

		/**
		 * LiveSImulation cytoscape graph.
		 * @type {cytoscape}
		 */
		this.cy = cytoscape({
			container: $(this.selector),

			boxSelectionEnabled: false,
			autounselectify: false,

			style: this.stylesheet
		})

		this.cy.minZoom(0.5)
		this.cy.maxZoom(2)

		// Import nodes and vertices from cy object.

		this.originalCy.nodes().forEach(node => {
			this.cy.add({
				data: {id: node.id()},
				position: node.position(),
				group: 'nodes',
				classes: `node${(node.id() === '0') ? ' base' : ''}`,
				selectable: false,
				locked: true,
				grabbable: false
			})
		})

		/**
		 * Base du gardien.
		 * 
		 * @type {Node}
		 */
		this.base = this.cy.nodes('[id = "0"]')

		this.originalCy.edges().forEach(edge => {
			this.cy.add({
				data: {
					id: edge.id(),
					source: edge.source().id(),
					target: edge.target().id()
				},
				group: 'edges',
				selectable: false,
				locked: true,
				grabbable: false
			})
		})

		// Add robber and guardian.

		this.cy.add({
			data: {id: 'robber'},
			position: {
				x: Math.cos(new Date()/1000) * 20,
				y: Math.sin(new Date()/1000) * 20
			},
			classes: 'robber',
			selectable: false,
			locked: false,
			grabbable: false
		})

		this.cy.add({
			data: {id: 'guardian'},
			position: Object.assign({}, this.cy.nodes('[id = "0"]').position()),
			classes: 'guardian',
			selectable: false,
			locked: false,
			grabbable: false
		})

		/**
		 * Node representing the robber.
		 * 
		 * @type {Node}
		 */
		this.robber = this.cy.nodes('#robber')

		/**
		 * Node representing the guardian.
		 * 
		 * @type {Node}
		 */
		this.guardian = this.cy.nodes('#guardian')
	}

	/**
	 * Launch a new iterration.
	 * 
	 * @return {LiveSimulation} chaining
	 */
	newIteration(){
		/**
		 * The robber's current target.
		 * @type {Node}
		 */
		this.robberTarget = this.randomTarget()

		/**
		 * Startup tmestamp (for the robber movement).
		 * @type {Date}
		 */
		this.iterationStart = new Date()

		/**
		 * How many ms before the robber hit the target?
		 * @type {Number}
		 */
		this.countdown = Math.random()*2500*this.cy.filter('.node').length + 2500

		/**
		 * The path the guardian will follow.
		 * @type {Array<Node>}
		 */
		this.guardianPath = this.randomPath()

		/**
		 * The last node the guardian visited.
		 * @type {Node}
		 */
		this.guardianLastVisit = this.base
		this.guardian.position(Object.assign({}, this.base.position()))

		/**
		 * The node the guardian is going to.
		 * @type {Node}
		 */
		this.guardianTarget = this.nextGuardianTarget(true)

		return this
	}

	/**
	 * Render the next step.
	 * 
	 * @return {void} undefined
	 */
	nextStep(){
		// fix a bug when graph is not showing on page change.
		this.cy.resize()
		this.cy.fit(this.cy.filter('.node'))

		// If the user dismiss the results, we stop the simulation.
		if ($(this.selector).length === 0)
			return console.info('Live simulation stopped.')

		const delta = (this.iterationStart.valueOf() + this.countdown - new Date().valueOf())/50

		if (delta <= 0)
			return this.robberHitTarget()

		this.robber.position({
			x: Math.cos(new Date()/1000) * delta + this.robberTarget.position().x,
			y: Math.sin(new Date()/1000) * delta + this.robberTarget.position().y
		})
		this.robber.data('refresh', Math.random())

		const guardianPosition = this.guardian.position()
		const targetPosition = this.guardianTarget.position()

		guardianPosition.x = guardianPosition.x * 0.95 + targetPosition.x * 0.05
		guardianPosition.y = guardianPosition.y * 0.95 + targetPosition.y * 0.05
		this.guardian.data('refresh', Math.random())

		if ((guardianPosition.x - targetPosition.x)**2 + (guardianPosition.y - targetPosition.y)**2 < 5){
			this.guardianTarget.addClass('secured')
			this.guardianLastVisit = this.guardianTarget
			const newGuardianTarget = this.nextGuardianTarget()
			if (newGuardianTarget !== null)
				this.guardianTarget = newGuardianTarget
			//else 
			//	return this.everyTargetIsSecured()
		}

		setTimeout(() => this.nextStep(), 50)
	}

	/**
	 * Handle what happen when the robber hit his targer.
	 * /!\ Is asynchronous because of LiveSimulation::iterationEnd use.
	 * 
	 * @return {LiveSimulation} chaining
	 */

	robberHitTarget(){
		if (!this.robberTarget.hasClass('secured')){
			this.robberTarget.addClass('robbed')
			$('#liveSimulationLog').text('Robbed!')
		} else {
			this.robberTarget.removeClass('secured').addClass('caught')
			$('#liveSimulationLog').text('Caught!')
		}
		return this.iterationEnd()

	}

	/**
	 * @return {Array<Node>}
	 */
	randomPath(){

		let fairDiceRoll = Math.random()

		let pathNumber = -1

		while(fairDiceRoll > 0){
			pathNumber++
			fairDiceRoll-=this.bestStreategy.probabilities[pathNumber]
		}

		return this.computedData.patrols[pathNumber].slice(1).map(nodeId => 
			this.cy.nodes(`[id = "${nodeId}"]`)[0]
		)
	}

	/**
	 * Call for another iteration when the running one finishes.
	 * 
	 * @return {LiveSimulation} chaining
	 */
	iterationEnd(){
		setTimeout(() => {
			this.cy.nodes().forEach(node => node.removeClass('secured').removeClass('robbed').removeClass('caught'))
			this.run()
			$('#liveSimulationLog').text('Iteration running...')
		}, 1000)

		return this
	}

	/**
	 * Give the next target the Guardian should go for.
	 * 
	 * @param  {Boolean} init - Is this the first target?
	 * @return {Node} next target for the guardian.
	 */
	nextGuardianTarget(init){
		if (init)
			return this.guardianPath[0]

		const index = this.guardianPath.indexOf(this.guardianTarget)
		if (index+1 === this.guardianPath.length)
			return null

		return this.guardianPath[index+1]
	}

	/**
	 * Get a random target according to the distribution (see RobbersInterest).
	 *
	 * @return {Node} a random target
	 */
	randomTarget(){
		let distribution = []
		this.originalCy.nodes('[id != "0"]').forEach(node => {
			for (let i=0;i<node.data('robbersInterest');i++)
				distribution.push(node.id())
		})

		let randomId = distribution[Math.floor(Math.random()*distribution.length)]

		return this.cy.nodes(`#${randomId}`)[0]
	}

	/**
	 * Start the simulation.
	 *
	 * @return {LiveSimulation} chaining
	 */
	run(){
		this.newIteration()
		this.nextStep()

		return this
	}

}

