			        "target": 1,
			        "length": 73.49447644360816
			      }
			    ]
			  },
			  "robbers": {
			    "list": [
			      0
			    ],
			    "catchProbability": {
			      "0": 0.5
			    }
			  }
			}
	}

	/**
	 * Load the settings after checking if they are corrupted or not.
	 *
	 * @param {Object} settings - JSON representation of the settings.
	 * @return {Loader} chaining
	 */
	load(settings){
		let corruptionMessage = this.checkCorruption(settings)
		if (corruptionMessage)
			console.error(corruptionMessage)

		this.settings.init()

		$('#numberOfIterations').val(settings.general.numberOfIterations)
		$('#distanceWeight').val(settings.general.distanceWeight)

		// Id maps (loaded ids => current ids)

		const verticesIdMap = new Map()
		const robbersIdMap = new Map()

		settings.robbers.list.forEach(robberId => {
			robbersIdMap.set(robberId, this.settings.robbers.newRobber(1 - settings.robbers.catchProbability[`${robberId}`]))
		})

		settings.paths.vertices.forEach(vertex => {

			verticesIdMap.set(vertex.id, this.settings.graph.addNode(vertex.position, vertex.id === 0, vertex.robbersInterest, vertex.guardiansCost, vertex.guardiansReward))

			const newNodeId = verticesIdMap.get(vertex.id)

			settings.robbers.list.forEach(robberId => {
				const newRobberId = robbersIdMap.get(robberId)

				this.settings.graph.cy.nodes(`[id = "${newNodeId}"]`).data('robberSettings').set(newRobberId, vertex.robberSettings[robberId])
			})

		})

		settings.paths.edges.forEach(edge => {
			this.settings.graph.link(
				verticesIdMap.get(edge.source), 
				verticesIdMap.get(edge.target))
		})

		this.settings.graph.cy.fit()

		console.log('Settings loaded')

		return this
	}

	/**
	 * Load the settings object from a JSON file on client's computer.
	 * /!\ May be asynchronous depending on browsers implementation.
	 * 
	 * @return {Loader} chaining
	 */
	import(){
		const input = document.createElement('input')
		input.setAttribute('type', 'file')

		input.style.display = 'none'

		input.addEventListener('change', event => {

			const file = input.files[0]

			const reader = new FileReader()
			reader.onload = event => {
				try {
					this.load(JSON.parse(atob(event.target.result.split(',').pop())))
				} catch (e) {
					console.error('The given config file was not a valid JSON file.')
				}
			}

			reader.readAsDataURL(file)

			document.body.removeChild(input)

		})

		document.body.appendChild(input)

		input.click()

		return this
	}

	/**
	 * Initialize the graph by setting default values.
	 * 
	 * @return {Loader} chaining
	 */
	loadDefault(){
		return this.load(this.defaultSettings)
	}

	/**
	 * Check the corruption of given settings as JSON object.
	 * 
	 * @param  {Object} settings - settings to check if they are corrupted. 
	 * @return {string | false} string (== true) describing the error 
	 * 	if corrupted, false else.
	 */
	checkCorruption(settings){

		// Fields presence

		if (typeof settings === 'undefined')
			return `No settings submitted.`

		if (typeof settings.general === 'undefined')
			return `No general settings submitted.`

		if (typeof settings.paths === 'undefined')
			return `No paths settings submitted.`

		if (typeof settings.paths.vertices === 'undefined')
			return `No vertices settings submitted.`

		if (typeof settings.paths.edges === 'undefined')
			return `No edges settings submitted.`

		if (typeof settings.robbers === 'undefined')
			return `No robbers settings submitted.`

		if (typeof settings.robbers.list === 'undefined')
			return `No robbers list submitted.`

		if (typeof settings.robbers.catchProbability === 'undefined')
			return `No catch probability settings submitted.`

		// settings integrity

		const robbersList = settings.robbers.list

		if (robbersList.length === 0)
			return `Robbers list should contain at least 1 robber.`

		const verticesSet = new Set()

		if (settings.general.numberOfIterations < 1 || settings.general.numberOfIterations > 100)
			return `Invalid number of iterations.`

		if (! settings.general.distanceWeight > 0)
			return `Invalid distance weight (must be > 0).`

		// Vertcies integrity

		for (let vertice of settings.paths.vertices){
			if (verticesSet.has(vertice.id))
				return `Same id (${vertice.id}) shared by two differents target.`

			verticesSet.add(vertice.id)

			if (! (vertice.robbersInterest >= 0))
				return `Invalid robbers interest for target ${vertice.id}.`

			if (! (vertice.guardiansCost >= 0))
				return `Invalid guardians cost for target ${vertice.id}.`

			if (! (vertice.guardiansReward >= 0))
				return `Invalid guardians reward for target ${vertice.id}.`

			if (typeof vertice.robberSettings === 'undefined')
				return `No robber settings submitted for target ${vertice.id}.`

			if (typeof vertice.position !== 'object')
				return `No position settings submitted for target ${vertice.id}.`

			if (typeof vertice.position.x !== 'number')
				return `Invalid x position submitted for target ${vertice.id}.`

			if (typeof vertice.position.y !== 'number')
				return `Invalid y position submitted for target ${vertice.id}.`

			for (let robber of robbersList){

				if (typeof vertice.robberSettings[robber] === 'undefined')
					return `No settings submitted for rober ${robber} and target ${vertice.id}.`

				if (! (vertice.robberSettings[robber].cost >= 0))
					return `Invalid robber cost for rober ${robber} and target ${vertice.id}.`

				if (! (vertice.robberSettings[robber].reward >= 0))
					return `Invalid robber reward for rober ${robber} and target ${vertice.id}.`
			}
		}

		if (! verticesSet.has(0))
			return `No base vertex submitted.`

		// Edges integrity

		for (let edge of settings.paths.edges){
			if (typeof edge.source === 'undefined')
				return `No source submitted for a given edge.`
			if (typeof edge.target === 'undefined')
				return `No target submitted for a given edge.`
			if (edge.length < 0)
				return `Invalid length submitted for a given edge.`
			if (! (verticesSet.has(edge.source)))
				return `The source ${edge.source} does not exist.`
			if (! (verticesSet.has(edge.target)))
				return `The target ${edge.target} does not exist.`
		}

		if (settings.paths.edges.length === 0)
			return 'The path should have at least 1 edge.'

		// Catch probability integrity

		for (let robber of robbersList){
			if (! (settings.robbers.catchProbability[robber] >= 0 
				&& settings.robbers.catchProbability[robber] <= 1))
				return `Invalid catch probability for robber ${robber}.`
		}

		return false
	}
}

/**
 * Class enabling you to download the current settings or whatever text you want
 * on the client computer without server-side.
 */
export default class Saver {

	/**
	* @param {Settings} settings - Settings object using this saver. 
	*/
	constructor(settings){
		/**
		 * Settings object using this loader.
		 * @type {Settings}
		 */
		this.settings = settings
	}

	/**
	 * Download the current settings.
	 * 
	 * @return {Saver} chaining
	 */
	save(){

		const date = new Date()

		return this.download(
			`${date.toLocaleDateString()}-${date.toLocaleTimeString().replace(':', '-')}.json`,
			JSON.stringify(this.settings.getSettings()))
	}

	/**
	 * Download a file with given name and content.
	 * /!\ May be asynchronous depending on browsers implementation.
	 * 
	 * @param  {string} filename - The file name.
	 * @param  {string} text - The file content.
	 * @return {Saver} chaining
	 */
	download(filename, text) {
		const link = document.createElement('a')
		link.setAttribute('href', `data:text/json;charset=utf-8,${encodeURIComponent(text)}`)
		link.setAttribute('download', filename)

		link.style.display = 'none'
		document.body.appendChild(link)

		link.click()

		document.body.removeChild(link)

		return this
	}
}

/**
*	Class representing the graph of the simulation.
*
*	You can add targets, delete targets, and link
*	them together.
*
*	For each target, you can set :
*		- robbersInterest (the probability of robbers attacking this target)
*		- guardiansCost (the cost when guardians fail to protect the target)
*		- guardiansReward (the reward when guardians manage to prevent 
*							an attack)
*		- robberSettings (the cost, reward and probability for each robber)
*			(Set through the Robbers class)
*
*	Nodes = Attacks = Targets
*/

export default class Graph {

	/**
	 * @param {Settings} settings - Settings object using this graph. 
	 */
	constructor(settings){

		// Fields
		/**
		 * Settings object using this graph.
		 * @type {Settings}
		 */
		this.settings = settings

		/**
		 * Graph style.
		 * @type {Object}
		 */
		this.stylesheet = [{
				selector: 'node',
				style: {
					height: 20,
					width: 20,
					'background-color': 'mapData(robbersInterest, 0, 25, green, red)',
					content: node => `N${node.data('id')} C${node.data('guardiansCost')}/R${node.data('guardiansReward')}`,
					//'text-valign': 'center',
					'text-halign': 'center'
				}
			},
			{
				selector: 'edge',
				style: {
					'curve-style': 'haystack',
					'haystack-radius': 0,
					width: 5,
					opacity: 0.5,
					'line-color': '#a2efa2',
					content: edge => Math.floor(this.length(edge))
				}
			},
			{
				selector: '.base',
				style: {
					'background-color': '#61bffc',
					label: 'Base'
				}
			},
			{
				selector: ':selected',
				style: {
					'border-width': 4,
					'border-color': 'purple'
				}
			}
		]

		/**
		 * Cytoscape graph linked to this graph.
		 * @type {cytoscape}
		 */
		this.cy = window.cy = cytoscape({
			container: $('#graph'),

			boxSelectionEnabled: false,
			autounselectify: false,

			style: this.stylesheet
		})

		this.cy.minZoom(0.5)
		this.cy.maxZoom(2)

		window.graph = this

		// Refreshing the length
		/**
		 * Interval that refresh the cytoscape graph.
		 * @return {Interval}
		 */
		this.refreshInterval = setInterval(() => cy.edges().forEach(edge => edge.data('refresh', Math.random())), 250)

		// DOM listeners

		$(document).on('click', '.qtip-content .link', event => {
			console.info("Linking a target to another...")
			/**
			 * What the user is currently doing.
			 * @type {String}
			 */
			this.currentAction = 'linking'
			$('.qtip').qtip('hide')
		})

		$(document).on('click', '.qtip-content .delete', event => {
			this.lastSelectedNode.remove()
			this.reset()
		})

		$(document).on('click', '.qtip-content .dismiss', event => {
			this.reset()
		})

		$(document).on('click', '.qtip-content .plusInterest', event => {
			this.lastSelectedNode.data('robbersInterest', Math.min(this.lastSelectedNode.data('robbersInterest')+1, 25))
		})

		$(document).on('click', '.qtip-content .minusInterest', event => {
			this.lastSelectedNode.data('robbersInterest', Math.max(this.lastSelectedNode.data('robbersInterest')-1, 0))
		})

		$(document).on('click', '.qtip-content .plusCost', event => {
			this.lastSelectedNode.data('guardiansCost', Math.min(this.lastSelectedNode.data('guardiansCost')+1, 25))
		})

		$(document).on('click', '.qtip-content .minusCost', event => {
			this.lastSelectedNode.data('guardiansCost', Math.max(this.lastSelectedNode.data('guardiansCost')-1, 0))
		})

		$(document).on('click', '.qtip-content .plusReward', event => {
			this.lastSelectedNode.data('guardiansReward', Math.min(this.lastSelectedNode.data('guardiansReward')+1, 25))
		})

		$(document).on('click', '.qtip-content .minusReward', event => {
			this.lastSelectedNode.data('guardiansReward', Math.max(this.lastSelectedNode.data('guardiansReward')-1, 0))
		})

		// Cytoscape listeners

		this.cy.on('tap', event => {
			if (event.target === event.cy)
				this.reset()
			// When you tap on the background and that there are no visible tips, you add a new node at this position.
			// If a tip is visible, you probably just want to dismiss it
			if (event.target === event.cy && !$('.qtip:visible').length){
				return this.addNode(event.position)
			}
		})

		this.cy.on('tap', 'node', event => {
			if (this.currentAction === 'linking'){
				this.currentAction = null
				const secondNode = event.target
				// We check if that edge aleady exists or if the source and target are the same node.
				if (!this.cy.elements(`edge[source = "${this.lastSelectedNode.id()}"][target = "${secondNode.id()}"]`).length && 
					!this.cy.elements(`edge[target = "${this.lastSelectedNode.id()}"][source = "${secondNode.id()}"]`).length && 
					secondNode != this.lastSelectedNode){
					this.link(this.lastSelectedNode.id(), secondNode.id())
				}
			}

			/**
			 * The node that was clicked the last.
			 * @type {Node}
			 */
			this.lastSelectedNode = event.target
		})

		this.cy.on('tap', 'edge', event => {
			event.target.remove()
		})

		// fix a bug when tap doesn't work on page change.

		$(document).on('click', event => this.cy.resize())

		this.init()
	}

	/**
	 * Initialize the graph by setting default values.
	 *
	 * @return {Graph} chaining
	 */
	init(){
		/**
		 * Number of edges created.
		 * Deleting an edge doesn't decrease that counter.
		 * 
		 * @type {Number}
		 */
		this.nbrEdgesCreated = 0

		/**
		 * Number of created nodes.
		 * Deleting a node doesn't decrease this counter.
		 * 
		 * @type {Number}
		 */
		this.nbrNodesCreated = 0

		/**
		 * The node that was clicked the last.
		 * @type {Node}
		 */
		this.lastSelectedNode = null

		/**
		 * What the user is currently doing.
		 * @type {String}
		 */
		this.currentAction = null

		this.cy.elements().forEach(element => element.remove())

		return this
	}

	/**
	 * Sort targets with the CoSE layout (by Bilkent University).
	 *
	 * @return {Graph} chaining
	 */
	sort(){
		this.cy.layout({
			name: 'cose-bilkent',
			animate: true
		}).run()

		return this
	}

	/**
	 * Reset the current action, selected target and hide the tips.
	 *
	 * @return {Graph} chaining
	 */
	reset(){
		this.lastSelectedNode = null
		this.currentAction = null
		$('.qtip').qtip('hide')

		return this
	}


	/**
	 * Link two targets together. You have to specify the ids.
	 * 
	 * @param  {string} source - source node id.
	 * @param  {string} target - target node id.
	 * @return {Graph} chaining
	 */
	link(source, target){
		this.cy.add({
			data: {
				id: `e${this.nbrEdgesCreated++}`,
				source: source,
				target: target
			},
			group: 'edges',
			selectable: true,
			locked: false,
			grabbable: true,
			classes: ''
		})
		console.info(`Edge added linking ${source} to ${target}.`)

		return this
	}

	/**
	 * Add a node to the graph.
	 *
	 * Base nodes can not been attacket nor defended.
	 * Patrols have to start at the base.
	 * 
	 * @param {Object} position - node coordinates.
	 * @param {Boolean} base - do you want to add a base (you shouldn't, except the first one)
	 * @param {Number} robbersInterest - How often will robbers attack this target compared to others.
	 * @param {Number} guardiansCost - How much will it cost to the guardian to not catch the robber on this node.
	 * @param {Number} guardiansReward - How much will a guardian earn from catching a robber on this node.
	 * @return {string} the new node id.
	 */
	addNode(position = {x: 0, y: 0}, base = false, robbersInterest = 1, guardiansCost = 1, guardiansReward = 1){
		const newNodeId = this.cy.nodes().length

		const newNode = this.cy.add({
			data: {
				id: newNodeId,
				robbersInterest: robbersInterest,
				guardiansCost: guardiansCost,
				guardiansReward: guardiansReward,
				robberSettings : new Map()
			},
			position: position,
			group: 'nodes',
			selectable: true,
			locked: false,
			grabbable: true,
			classes: base ? 'base' : ''
		}).qtip({
			content: `
			<div>
				<a class="waves-effect waves-light btn blue link" style="width:160px"><i class="material-icons right">timeline</i>Link to...</a>
				<a class="waves-effect waves-light btn red delete" style="width:160px; margin-top: 10px" ${base ? 'disabled' : ''}><i class="material-icons right">delete</i>Delete</a>

				<a class="waves-effect waves-light btn red lighten-2 minusInterest col" ${base ? 'disabled' : ''}><i class="material-icons">remove_circle</i></a>
				<div class="label">Robbers Interest</div>
				<a class="waves-effect waves-light btn green lighten-2 plusInterest col" ${base ? 'disabled' : ''}><i class="material-icons">add_circle</i></a>

				<a class="waves-effect waves-light btn red lighten-2 minusCost col" ${base ? 'disabled' : ''}><i class="material-icons">remove_circle</i></a>
				<div class="label">Guardians Cost</div>
				<a class="waves-effect waves-light btn green lighten-2 plusCost col" ${base ? 'disabled' : ''}><i class="material-icons">add_circle</i></a>

				<a class="waves-effect waves-light btn red lighten-2 minusReward col" ${base ? 'disabled' : ''}><i class="material-icons">remove_circle</i></a>
				<div class="label">Guardians Reward</div>
				<a class="waves-effect waves-light btn green lighten-2 plusReward col" ${base ? 'disabled' : ''}><i class="material-icons">add_circle</i></a>

				<a class="waves-effect waves-light btn green dismiss" style="width:160px; margin-top: 10px"><i class="material-icons right">cancel</i>Dismiss</a>
			</div>
			`,
			position: {
				my: 'top center',
				at: 'bottom center'
			},
			style: {
				classes: 'qtip-bootstrap',
				width: 195
			}
		})

		this.settings.robbers.list.forEach(robber => newNode.data('robberSettings').set(robber, {
			cost: 2,
			reward: 1
		}))

		return newNodeId
	}

	/**
	 * Return the length of an edge.
	 * 
	 * @param  {Edge} edge - the edge to measure.
	 * @return {Number} the length.
	 */
	length(edge){
		return this.distance(edge.source(), edge.target())
	}

	/**
	 *	Return the distance between dwo vertices.
	 *
	 * @param  {Node} node1 - the first node.
	 * @param  {Node} node2 - the second node.
	 * @return {Number} the distance.
	 */
	distance(node1, node2){
		return ((node1.position().x - node2.position().x)**2 + (node1.position().y - node2.position().y)**2)**0.5
	}

	/**
	 *	Concatenate settings into a JSON object.
	 *	
	 * @return {Object} concatenated settings. 
	 */
	getSettings(){
		return {
			vertices: Object.keys(cy.nodes())
							 .filter(key => !isNaN(key))
							 .map(key => ({
							 	id: parseInt(cy.nodes()[key].id()),
							 	position: cy.nodes()[key].position(),
							 	robbersInterest: cy.nodes()[key].data('robbersInterest'),
							 	guardiansCost: cy.nodes()[key].data('guardiansCost'),
								guardiansReward: cy.nodes()[key].data('guardiansReward'),
								robberSettings: Array.from(cy.nodes()[key].data('robberSettings')).reduce((obj, [key, value]) => { obj[key] = value; return obj}, {})
							 })),
			edges: Object.keys(cy.edges())
						 .filter(key => !isNaN(key))
						 .map(key => ({
						 	source: parseInt(cy.edges()[key].source().id()),
						 	target: parseInt(cy.edges()[key].target().id()),
						 	length: this.length(cy.edges()[key])
						 }))
		}
	}
}

/**
 * Sub-setting for robbers manipulation.
 */
export default class Robbers{

	/**
	* @param {Settings} settings - Settings object using this robbers. 
	*/
	constructor(settings){

		// Fields
		/**
		 * Settings object using this robbers.
		 * @type {Settings}
		 */
		this.settings = settings

		// DOM listeners

		$(document).on('click', '#robbers .delete', event => {

			this.removeRobber($(event.currentTarget).parent().parent().parent().data('robberid'))

		})

		$(document).on('click', '#robbers .configure', event => {

			this.configureRobber($(event.currentTarget).parent().parent().parent().data('robberid'))

		})

		$(document).on('change', '#robbers input.discretion', event => {

			const newValue = 1-parseFloat($(event.currentTarget).val())

			if (newValue < 0 || newValue > 1){
				return $(event.currentTarget).css({
					color: 'red'
				})
			}

			$(event.currentTarget).css({
				color: "#fff"
			})

			this.catchProbability.set(
				$(event.currentTarget).parent().parent().parent().parent().data('robberid'),
				newValue
			)

		})

		$(document).on('change', '#modal-robber-config input', event => {

			const row = $(event.currentTarget).parent().parent()

			const nodeId = row.data('nodeid')
			const robberId = row.data('robberid')

			const setting = $(event.currentTarget).data('setting')
			const newValue = parseFloat($(event.currentTarget).val())

			console.info(`${setting} changed for target ${nodeId}, new value is ${newValue}.`)

			this.settings.graph.cy.nodes(`[id = "${nodeId}"]`).data('robberSettings').get(robberId)[setting] = newValue

		})

		this.init()

	}

	/**
	 * Initialize the robbers by setting default values.
	 *
	 * @return {Robbers} chaining
	 */
	init(){

		if (typeof this.list !== 'undefined')
			[...this.list].forEach(robberId => this.removeRobber(robberId))

		/**
		 * Number of robbers created until now.
		 * Deleting a robber doesn't decrease that counter.
		 * 
		 * @type {Number}
		 */
		this.numberOfRobbersCreated = 0

		/**
		 * Set of all robbers.
		 * @todo We should probably change the name of this variable.
		 * 
		 * @type {Set<Object>}
		 */
		this.list = new Set()

		/**
		 * Probability of beeing caught for each robber.
		 *
		 * @type {Map<Number, Number>}
		 */
		this.catchProbability = new Map()

		return this
	}

	/**
	 * Add a robber to the settings.
	 * His card can be seen in the "Robbers" tab.
	 * His settings are set to default in every target.
	 *
	 * @param {Number} catchProbability - the catch probability of the new robber.
	 * @return {Number} the new robber id.
	 */
	newRobber(catchProbability = 0.5){

		const robberId = this.numberOfRobbersCreated++

		this.list.add(robberId)

		this.catchProbability.set(robberId, catchProbability)

		this.settings.graph.cy.nodes().each(node => node.data('robberSettings').set(robberId, {
			cost: 1,
			reward: 1
		}))

		$('#robbers').append(`
			<div class="col s4" data-robberid="${robberId}">
			    <div class="card blue-grey darken-1">
					<div class="card-content white-text">
						<span class="card-title">Robber ${robberId+1}</span>
						<!--<p>Some bad guy.</p>-->
					</div>
					<div class="card-action">
						<div class="discretionContainer">
							<span>Discretion</span>
							<input type="number" step="0.05" class="discretion" min="0" max="1" value="${(1-catchProbability)}">
						</div>
						<a class="waves-effect waves-light btn blue configure" style="width: 100%; margin-top: 10px;"><i class="material-icons right">mode_edit</i>Rewards</a>
						<a class="waves-effect waves-light btn red delete" style="width: 100%; margin-top: 10px" ${(robberId === 0) ? 'disabled' : ''}><i class="material-icons right">delete</i>Delete</a>
					</div>
				</div>
			</div>
		`)

		return robberId
	}

	/**
	 * Remove a robber from the settings.
	 * His card gets removed and references to his settings are
	 * removed from each target.
	 *
	 * @param {Number} robberId - the robber to remove.
	 * @return {Robbers} chaining
	 */
	removeRobber(robberId){

		console.info(`Removing robber ${robberId}...`)

		this.list.delete(robberId)

		this.settings.graph.cy.nodes().each(node => node.data('robberSettings').delete(robberId))

		$('#robbers').find(`[data-robberid=${robberId}]`).remove()

		return this
	}

	/**
	 * Display a modal enabling the user to set the
	 * robber properties for every target.
	 * 
	 * @param {Number} robberId - the robber to configure.
	 * @return {Robbers} chaining
	 */
	configureRobber(robberId){

		console.info(`Configuring robber ${robberId+1}.`)

		let table = `
			<table class="striped centered">
				<thead>
					<tr>
						<th>Target ID</th>
						<th>Cost</th>
						<th>Reward</th>
					</tr>
				</thead>

				<tbody>`

		this.settings.graph.cy.nodes('[id != "0"]').forEach(node => {

			let settings = node.data('robberSettings').get(robberId)

			table += `
				<tr data-nodeid="${node.id()}" data-robberid="${robberId}">
					<td>${node.id()}</td>
					<td><input data-setting="cost" type="number" value="${settings.cost}" min="0"></td>
					<td><input data-setting="reward" type="number" value="${settings.reward}" min="0"></td>
				</tr>`
		})

		table += `
				</tbody>
			</table>`

		$('#modal-robber-config h4').text(`Robber ${robberId+1} configuration`)

		$('#modal-robber-config p').html(table)

		$('#modal-robber-config').modal('open')

		return this
	}

	/**
 	 * Return the list of every robber.
	 *
	 * @return {Object} Robbers settings.
	 */
	getSettings(){
		return {
			list: [...this.list],
			catchProbability: Array.from(this.catchProbability).reduce((obj, [key, value]) => { obj[key] = value; return obj}, {})
		}
	}


}

import Interface from './classes/Interface'

/*
*	Cytoscape (the graph library we are using) doesn't work so
*	well when the rendering canvas is hidden while the graph
*	is initialized. We have to wait for the canvas to be displayed
*	before initializing it and to only do so once.
*
*	Thus, we use the global flag graphInitialized.
*/


let graphInitialized = false

/**
 *	Function called whenever the hash is updated to do the correct
 *	action.
 */

const updateHash = hash => {

	// We remove the '#' character from the hash. Just in case.
	hash = hash.replace( /^#/, '' )

	/*
	*	Prevents # links from going to the element.
	*/
	const node = $( `#${hash}` )
	node.attr( 'id', '' )
	document.location.hash = hash
	node.attr( 'id', hash )

	/*
	*	We have to sort the graph when it's displayed
	*	for the first time.
	*/
	if (!graphInitialized && hash === 'simulate'){
		window.graph.sort()
		graphInitialized = true
	}

	if (window.cy !== undefined)
		window.cy.resize()

	/*
	*	Fix a bug with parallax images.
	*/

	setTimeout(() => {
		$(window).scroll().resize()
	}, 25)
}

/**
 *	Setup non-specific DOM listeners and initialize modules.
 */
const setupDOM = () => {

	$('[data-dest]').click(event => {
		window.location.hash = $(event.eventTarget).data('dest')
		$('nav ul.tabs').tabs('select_tab', $(event.currentTarget).data('dest'))
		updateHash($(event.currentTarget).data('dest'))
	})

	$('nav ul.tabs').on('click', 'a', event => {
		updateHash($(event.currentTarget).attr('href'))
	})

	$(window).on('hashchange', () => {
		$('nav ul.tabs').tabs('select_tab', window.location.hash.slice(1))
		updateHash(window.location.hash)
	})

	$('.parallax').parallax()

	$('.modal#modal-robber-config').modal()

	ConsoleLogHTML.connect($('#console'))
}

/**
 *	Whenever the DOM content is reaady to be manipulated,
 *	setupe the specific DOM and create an Interface with the server.
 *	Then, we link the UI elements to the settings they manipulate.
 */
$(() => {
	setupDOM()

	const iface = new Interface()
	$('#sortNodes').on('click', event => iface.settings.graph.sort())
	$('#newRobber').on('click', event => iface.settings.robbers.newRobber())
	$('#launchButton').on('click', event => iface.startSimulation())
	$('#importButton').on('click', event => iface.settings.loader.import())
	$('#exportButton').on('click', event => iface.settings.saver.save())
	$('#export-gambit').on('click', event => iface.results.exportGambit())
	$('.modal#modal-results').modal({complete: () => {
		iface.stopSimulation()
		$('#export-gambit').attr('disabled', 'true')
	}})
})
